const navSelector=".c-nav",nav=document.querySelector(".c-nav"),burgerSelector=".burger",burger=document.querySelector(".burger");let onClickBurger=function(){this.classList.contains("-active")?(this.classList.remove("-active"),nav.classList.add("-close"),nav.classList.remove("-open")):(this.classList.add("-active"),nav.classList.add("-open"),nav.classList.remove("-close"))};burger.addEventListener("click",onClickBurger);