const controller=new ScrollMagic.Controller;let triggers=document.querySelectorAll(".trigger");var triggerlength=triggers.length;const avantage=document.querySelector(".scroll-avantage"),about=document.querySelector(".scroll-about");var page="",screenH=window.innerHeight;let offset=screenH/6;var main=function(){if(null!=triggers){if(checkPage(),page===avantage)var e=1e3;else if(page===about)e=screenH*triggerlength;scroll(e),active(e)}else console.log("no triggers")},checkPage=function(){return null!=avantage?page=avantage:null!=about&&(page=about),page},scroll=function(e){new ScrollMagic.Scene({triggerElement:"#trigger1",duration:e}).setPin("#pin1").addTo(controller)},active=function(e){let t=e/triggerlength;for(var r=1;r<triggerlength+1;r++)new ScrollMagic.Scene({triggerElement:"#trigger"+r,duration:t-offset}).setClassToggle("#txt"+r,"-active").addTo(controller)};main();