console.log("load-scrollmagic");const controller=new ScrollMagic.Controller;let triggers=document.querySelectorAll(".trigger");var triggerlength=triggers.length;const avantage=document.querySelector(".scroll-avantage"),about=document.querySelector(".scroll-about");var page="",screenH=window.innerHeight;let offset=screenH/6;var main=function(){if(null!=triggers){if(checkPage(),page===avantage)var e=1e3;else if(page===about)e=screenH*triggerlength;scroll(e),active(e)}else console.log("no triggers")},checkPage=function(){return null!=avantage?page=avantage:null!=about&&(page=about),page},scroll=function(e){new ScrollMagic.Scene({triggerElement:"#trigger1",duration:e}).setPin("#pin1").addTo(controller)},active=function(e){let r=e/triggerlength;for(var t=1;t<triggerlength+1;t++)new ScrollMagic.Scene({triggerElement:"#trigger"+t,duration:r-offset}).setClassToggle("#txt"+t,"-active").addTo(controller)};main();